$(document).ready(function() {

  $(document).on('click','#send', function() {     
    $.ajax({
      dataType: "JSON",
      url: "contact-process.php",
      type: "POST",
      data: $('#contactForm').serialize(),
      beforeSend: function(xhr) {
        // $('#login-alert').removeClass('red blue orange green'); // reset class every click
        // $('#error-area').removeClass('gone');
        $('#msg').removeClass('alert');
        $('#send-area').html('<div class="working"><div class="lds-ellipsis"><div></div><div></div><div></div><div></div></div></div>');

      },
      success: function(response) {
        console.log(response);
        if(response) {
          // console.log(response);
          if(response['signal'] == 'ok') {

            $('#contactForm').html('<ul class="contact-form"><div id="msg" class="success"><span>Your message was sent successfully!</span></div><div id="send-area"><div id="reset">Reset</div></div></div></ul>');

            $('#reset').addClass('reset-visible');

          } else {
            $('#send-area').html('<div id="send">Try again</div>')
            $('#msg').addClass('alert');
            $('#msg').html(response['li']);
          }
        } 
      },
      error: function(response) {
        // console.log(response);
        $('#login-btn').html(response['li']);
      }, 
      complete: function() {

      }
    })

  });
  $(document).on('click','#reset', function() {
    $('#contactForm').load('contact-reset.php');
  });
});
